import logo from './logo.svg';
import './App.css';
import {letturaDati} from './controlli';
import React from 'react';

class App extends React.Component{
  constructor(props){

    super(props);
    this.state=null;
   
    

  }
render(){console.log("prova"); return (
<div className="container">
<div className="row">  
<div className="search"  style={{textAlign:"center"}}>
  <h1 >Codicedelvino.it</h1>
 <input type="Text" onChange={changeEvent=>{letturaDati(changeEvent).then(x=>this.setState( {risultato:x})) }}  className="form-control" placeholder="Cerca per codice vino/tipologia"  />
 <select name="cars" id="cars">
  <option value="volvo">Volvo</option>
  <option value="saab">Saab</option>
  <option value="mercedes">Mercedes</option>
  <option value="audi">Audi</option>
</select>
 <div>
 <table style={{"margin":"auto","color":"grey"}} className="table table-striped">
  
 <thead>
   <tr >
    
    <th>Ragione</th>
    <th>Codice vino</th>
    <th>Descrizione</th>
  </tr>
   </thead>
   {this.state != null  ?  this.state.risultato.map(x => {return (

<tbody>
  <tr>
    <td> {x.regione}</td>
  
    <td> {x.codicevino}</td>
    <td> {x.descrizione}</td>
    
  </tr>
  </tbody>

   ) }) : ''} 
   </table> </div>
 
  
</div></div>

</div>

)
}





}

export default App;
